import React, { useEffect, useState } from "react"
import { DropdownItem } from "../Components/DropdownItem"
// import DropdownButton from "react-bootstrap/DropdownButton"
// import Dropdown from "react-bootstrap/Dropdown"

export const DropdownPage = () => {
    const [years, setYears] = useState([])
    const [selectedYear, setSelectedYear] = useState("")

    useEffect(() => {
        fetch("http://127.0.0.1:5000/api", {
            method: "GET",
            headers: {
                "Content-type": "application/json",
            },
        })
            .then((res) => res.json())
            .then((data) => {
                setYears(data)
            })
    }, [])

    const handleDropdownSelect = (e) => {
        console.log(e)
        setSelectedYear(e.target.value)
    }
    const handleDropdownSubmit = (e) => {
        e.preventDefault()
        fetch("http://127.0.0.1:5000/api/create", {
            method: "POST",
            body: JSON.stringify({ content: selectedYear }),
            headers: {
                "Content-Type": "application/json",
            },
        })
            .then((response) => response.json())
            .then((response) => {
                setTodo("")
                getLatestTodos()
            })
    }
    return (
        <div>
            <form onSubmit={handleDropdownSubmit}>
                <select onChange={handleDropdownSelect}>
                    <option value="">Select Year</option>
                    {years.map((year) => (
                        <DropdownItem key={year.id} year={year}></DropdownItem>
                    ))}
                </select>
            </form>
        </div>
    )
}
